import React from 'react';

const OfferComponent = ({ offerTitle, companyName, categories, salary, companyPhoto }) => {
  const handleClick = () => {
    // Tutaj możesz dodać obsługę kliknięcia w ofertę
    console.log(`Clicked on offer: ${offerTitle}`);
  };

  return (
    <div className="offer" style={{ backgroundColor: '#f2f2f2', border: '1px solid #ccc', padding: '10px', margin: '10px', borderRadius: '5px', cursor: 'pointer' }} onClick={handleClick}>
      <h3>{offerTitle}</h3>
      <p>{companyName}</p>
      <p>{categories}</p>
      <p>{salary}</p>
      <img src={companyPhoto} alt="Company" style={{ maxWidth: '100px' }} />
    </div>
  );
};

export default OfferComponent;
