import React from 'react';

const OfferComponent = ({ offerTitle, companyName, categories, salary, companyPhoto }) => {
  const handleClick = () => {
    // Tutaj możesz dodać obsługę kliknięcia w ofertę
    console.log(`Clicked on offer: ${offerTitle}`);
  };

  return (
    <div className="offer" style={{ backgroundColor: '#f2f2f2', border: '1px solid #ccc', padding: '10px', margin: '10px', borderRadius: '5px', cursor: 'pointer', display: 'flex', alignItems: 'center' }} onClick={handleClick}>
      <img src={companyPhoto} alt="Company" style={{ maxWidth: '100px', marginRight: '10px' }} />
      <div>
        <h3>{offerTitle}</h3>
        <p>{companyName}</p>
      </div>
      <div style={{ marginLeft: 'auto', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <div style={{ backgroundColor: 'lightblue', padding: '5px 10px', marginBottom: '5px' }}>{salary}</div>
        <div style={{ backgroundColor: 'lightgreen', padding: '5px 10px' }}>{categories}</div>
      </div>
    </div>
  );
};

export default OfferComponent;
