import React, { useState } from 'react';
import OfferComponent from './OfferComponent';
import Pagination from './Pagination';

const OfferListComponent = () => {
  const offers = [
    { id: 1, title: 'Offer 1', companyName: 'Company A', categories: 'Category A', salary: '1000', companyPhoto: 'company-a.jpg' },
    { id: 2, title: 'Offer 2', companyName: 'Company B', categories: 'Category B', salary: '2000', companyPhoto: 'company-b.jpg' },
    // ...
    // Reszta danych ofert
  ];

  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;
  const totalItems = offers.length;

  // Obliczanie indeksów początkowego i końcowego elementu dla bieżącej strony
  const indexOfLastItem = Math.min(currentPage * itemsPerPage, totalItems);
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentOffers = offers.slice(indexOfFirstItem, indexOfLastItem);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  return (
    <div className="offer-list">
      <h2>Offers</h2>
      {currentOffers.map((offer) => (
        <OfferComponent key={offer.id} offerTitle={offer.title} companyName={offer.companyName} categories={offer.categories} salary={offer.salary} companyPhoto={offer.companyPhoto} />
      ))}
      <Pagination totalItems={totalItems} itemsPerPage={itemsPerPage} currentPage={currentPage} onPageChange={handlePageChange} />
    </div>
  );
};

export default OfferListComponent;
